script:
frappe.ui.form.on('Stock Ledger Entry', {
	refresh(frm) {
		// your code her
		if (frm.doc.actual_qty <= (frm.doc.reorder_level * 0.1)) {  // Example: 20% threshold
            frappe.msgprint({
                title: __('Low Stock Alert'),
                indicator: 'red',
                message: `Stock for ${frm.doc.item_code} has reached 20% of its reorder level.`
            });
        }
	}
});
